INSERT INTO `posts.bak` (`id`, `title`, `term_id`, `slug`, `excerpt`, `content`, `status`, `is_draft`, `author`, `taxonomy`, `language`, `ordering`, `hits`, `is_pin`, `modified_time`, `created_time`, `publish_time`) VALUES
(52, 'Tự tay làm từ A - Z món đá bào Patbingsu hấp dẫn nhất của Hàn Quốc', 26, 'tu-tay-lam-tu-a-z-mon-da-bao-patbingsu-hap-dan-nhat-cua-han-quoc', 'Cùng vào bếp trổ tài với phiên bản patbingsu homemade thơm ngọt, ngon như ngoài hàng nhé!', '<div class="clearfix" style="outline: none; border: 0px; margin: 0px; padding: 0px; font-family: ''Times New Roman''; font-size: 16px; line-height: 14.5454540252686px;"><h2 class="sapo" style="outline: none; border: 0px; margin: 15px 0px; padding: 0px; line-height: 22px; font-weight: 700; font-size: 14px !important;">Cùng vào bếp trổ tài với phiên bản patbingsu homemade thơm ngọt, ngon như ngoài hàng nhé!</h2></div><div class="content " style="outline: none; border: 0px; margin: 0px; padding: 0px; line-height: 22px; font-family: ''Times New Roman''; font-size: 16px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Nguyên liệu để làm món patbingsu bao gồm:</span><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><br><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 225g đậu đỏ&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 1l nước&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 225g đường cát&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 5ml tinh chất vanilla&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- Môt chút muối&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 1 quả chuối&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 1 quả kiwi&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- 5 quả dâu&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- Bánh nếp Hàn Quốc&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- Sữa đặc có đường&nbsp;</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);">- Máy làm đá bào</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; font-style: italic; color: rgb(0, 0, 205);"><br></span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Cùng bắt tay vào thực hiện nào:</span></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><br></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><table cellspacing="0" cellpadding="0" border="0" style="outline: none; border: 0px; margin: 0px 0px 10px; padding: 0px;"><tbody><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 20px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-1" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoob1-87e05.jpg" alt="" style="cursor: none;"></div><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 1:<br></span><br><i>-&nbsp;Đầu tiên, bạn rửa sạch đậu đỏ với nước.</i><br><br></td></tr><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 15px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-2" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoo2-87e05.jpg" alt=""></div><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 2:<br></span><br><i>- Trút đậu đã rửa sạch vào nồi có đáy dày, thêm nước và đun sôi với lửa nhỏ trong vòng 10 phút. Sau đó, mình giảm lửa tới mức thấp nhất và đun đậu sôi liu riu thêm 1 tiếng nữa.</i></td></tr><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 15px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-3" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoo%20b3-87e05.jpg" alt=""></div><br><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 3:</span><br><br><i>- Khi kiểm tra thấy đậu đã chín mềm, ta đổ ra rây, chắt bỏ nước.</i><br><br><i>- Tiếp đó, cho đậu và nồi và thêm đường, tinh chất vanilla, muối vào rồi trộn đều nhẹ tay.</i><br><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px;"><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><br></td></tr><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 15px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-4" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoob4-87e05.jpg" alt=""></div><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 4:<br></span><br><i>- Bây giờ, mình đun phần đậu đã nêm đường trên lửa nhỏ khoảng 5 phút. Bạn nhớ đừng đậy nắp và khuấy nhẹ tay nhé.<br><br>- Sau đó, mình tắt lửa, để nguội và đặt phần đậu hầm này vào tủ lạnh.</i></td></tr><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 15px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-5" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoo5-87e05.jpg" alt=""></div><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 5:<br></span><br><i>- Bạn cắt trái cây thành các miếng nhỏ vừa ăn.</i><br><br></td></tr></tbody></table></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><table cellspacing="0" cellpadding="0" border="0" style="outline: none; border: 0px; margin: 0px 0px 10px; padding: 0px;"><tbody><tr style="outline: none; border: 0px; margin: 0px; padding: 0px;"><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 15px; padding-bottom: 25px; width: 300px;"><div class="warpt-share-fbPhoto" id="btn-share-img-6" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoo6-87e05.jpg" alt=""></div><br></td><td valign="top" style="outline: none; border: 0px; margin: 0px; padding-right: 5px; padding-left: 5px;"><span style="outline: none; border: 0px; margin: 0px; padding: 0px; font-weight: bold; text-decoration: underline;">Bước 6:<br></span><br><i>- Cuối cùng, mình xúc khoảng 100g đậu đỏ hầm vào ly. Sau đó, trút đá bào lên trên và xếp trái cây ở lớp trên cùng. Rưới sữa đặc và rắc một ít bánh nếp đã được luộc chín, cắt nhỏ nữa là đã hoàn thành rồi.</i></td></tr></tbody></table></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><b>Patbingsu của chúng ta đã hoàn thành rồi đây:</b></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><br></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px;"><div style="outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;"><div class="warpt-share-fbPhoto" id="btn-share-img-7" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/thumb_w/600/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/bingsoosp1-87e05.jpg" w="600" h="650" alt="" style="cursor: none;"></div></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;"><i>Trang trí thêm với chú cá nhỏ bằng kẹo dẻo nhìn vừa đáng yêu, ăn cũng rất ngon nữa.</i></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;"><i><br></i></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;"><div class="warpt-share-fbPhoto" id="btn-share-img-8" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/thumb_w/600/kftsA9wNGm0gwGKRZ8ACoyRW9w6z/Image/2015/06/5e2f13977b568195f5ac6fc95b756a64-6f266.jpg" w="700" h="1050" alt="" style="cursor: none;"></div><br><i>Patbingsu mát rượi, ngọt ngào thế này thì còn gì bằng nhỉ!</i></div><div style="outline: none; border: 0px; margin: 0px; padding: 0px; text-align: center;"><i><br></i><div class="warpt-share-fbPhoto" id="btn-share-img-9" style="outline: none; border: 0px; margin: 0px; padding: 0px; position: relative; display: inline-block;"><img src="http://k14.vcmedia.vn/thumb_w/600/MEnkxk8h73O5MAt0cRhvnbN4x1nLT/Image/2015/06/kit-steps/patbingsoo/sp3-0278d.jpg" w="600" h="400" alt="" style="cursor: none;"></div><br><i>Bạn có thể ăn kèm patbingsu với kem viên ngọt ngào béo ngậy cũng rất hợp đó!</i></div></div></div></div>', 'PUBLISH', 0, 'Luu Trong Hieu', 'post', 'vi-VN', 0, 0, 0, '2015-06-28 09:39:14', '2015-06-28 09:38:42', '0000-00-00 00:00:00'),
